package com;

public class Pattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		   for(int i=1;i<=4;i++)
		     {
		         int p=8;
		         for(int j=1;j<=i;j++)
		         {
		             System.out.print(p);
		            p=p-2;            
		         }
		         System.out.println();
		     }
	}

}
