package com;

public class Pattern2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 for(int i=4;i>=1;i--)
	     {
	         
	         for(int j=i;j>=1;j--)
	         {
	             System.out.print(j*2);
	            
	         }
	         System.out.println();
	     }
		    
	}

}
