package com;

public class Pattern3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  for(int i=1;i<=4;i++)
		     {
		         
		         for(int j=1;j<=i;j++)
		         {
		             System.out.print(j*i);
		            
		         }
		         System.out.println();
		     }
			    
	}

}
