package com;

public class Pattern7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        for(int i=0;i<=3;i++)
        {
        	for(int j=i;j<=i+i;j++)
        	{
        		System.out.print(j);
        
        	}
        	System.out.println();
        }
	}

}
