package com;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=2;
		 switch(a)
		 {
		     case 1:
		     System.out.println("suraj");
		     break;
		     
		     case 2:
		     System.out.println("bhushan");
		    break;
		    
		     case 10:
		    System.out.println("nikhil");
		    break;
		    default:
		    System.out.println("sanket");
		             
		                 
		 }
	}

}
